package condition;

public class IfElse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String s1 = "test";
		String s2= "automation";
		if(s1.contains("test")) {
			System.out.println("test will be printed");
		}else if(s2.contains("automation")) {
			System.out.println("automation will be printed");
		}else {
			System.out.println("nothing to be printed");
		}
				

	}

}
